        LineNumberReader  lnr = new LineNumberReader(new FileReader(targetFile));
        lnr.skip(Long.MAX_VALUE);
        fileLinesCount = lnr.getLineNumber();
        log.info(fileLinesCount + " hits approximately in all results");
