                BlastResultBuilder resultBuilder = new BlastResultBuilder();
                resultBuilder
                        .setQueryID(queryId)
                        .setDbFile(databaseFile)
                        .setProgram(programName)
                        .setQueryDef(queryName)
                        .setReference(blastReference);

